class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        vector<vector<pair<int, int>>> sortedRows(m);
        map<long long,vector<pair<long long,long long>>,greater<long long>>mp;
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                sortedRows[i].emplace_back(board[i][j], j);
            }
            sort(sortedRows[i].rbegin(), sortedRows[i].rend());
            for(int j=0;j<3;++j)
            {
                mp[sortedRows[i][j].first].push_back({i,sortedRows[i][j].second});
            }
        }

        long long maxSum = LLONG_MIN;
        for (int i1 = 0; i1 < m; ++i1) {
            for (int i2 = i1 + 1; i2 < m; ++i2) {

                for (int a = 0; a < 3; ++a) {
                    for (int b = 0; b < 3; ++b) {
                        int yyy1=sortedRows[i1][a].second,yyy2=sortedRows[i2][b].second;
                        if (sortedRows[i1][a].second != sortedRows[i2][b].second)
                        {
                            long long tmp=sortedRows[i1][a].first+sortedRows[i2][b].first;
                            for(auto &[i,j]:mp)
                            {
                                bool flag=1;
                                for(auto [x,y]:j)
                                {
                                    if(x!=i1&&x!=i2&&y!=yyy1&&y!=yyy2)flag=0;
                                    if(!flag)break;
                                }
                                if(!flag)
                                {
                                    maxSum=max(maxSum,tmp+i);
                                    break;
                                }
                            }
                        }
                    }
                }


            }
        }
        return maxSum;
    }
};
