class Solution {
    public long maximumValueSum(int[][] board) {
        PriorityQueue<long[]> heap = new PriorityQueue<>((a,b)->{
            return -Long.compare(a[0],b[0]);
        });
        int m = board.length;
        int n = board[0].length;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                heap.add(new long[]{board[i][j],i,j});
            }
        }
        PriorityQueue<long[]> heap1 = new PriorityQueue<>(heap);

        long ans = Long.MIN_VALUE;
        for(int i=0;i<4;i++){
            long tmp1 = 0;
            long[] poll1 = heap1.poll();
            tmp1+=poll1[0];
            long i1 = poll1[1];
            long j1 = poll1[2];
            PriorityQueue<long[]> heap2 = new PriorityQueue<>(heap);
            for(int j=0;j<3;j++){
                while (heap2.peek()[1]==i1||heap2.peek()[2]==j1){
                    heap2.poll();
                }
                long[] poll2 = heap2.poll();
                long tmp2 = tmp1;
                tmp2 += poll2[0];
                long i2 = poll2[1];
                long j2 = poll2[2];

                PriorityQueue<long[]> heap3 = new PriorityQueue<>(heap);
                while (heap3.peek()[1]==i1||heap3.peek()[2]==j1||heap3.peek()[1]==i2||heap3.peek()[2]==j2){
                    heap3.poll();
                }
                long tmp3 = tmp2;
                tmp3+=heap3.peek()[0];
                ans=Math.max(ans,tmp3);
            }
        }
        return ans;
    }
}
