    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<int>>answers(board.size());
        for(int i=0;i<board.size();i++){
            vector<pair<int,int>>p;
            for(int j=0;j<board[i].size();j++){
                p.push_back({board[i][j],j});
            }
            sort(p.begin(),p.end());
            reverse(p.begin(),p.end());
        vector<int>helper;
            for(int j=0;j<3;j++){
                helper.push_back(p[j].second);
            }
            answers[i]=helper;
        }
        long long answ=-10000000000;
        for(int i=0;i<board.size();i++){
            for(int j=0;j<board.size();j++){
                if(i==j){
                    continue;
                }
                    long long val=ans(i,j,board,answers);
                    answ=max({answ,val});

            }
        }
        return answ;
    }
