function maximumValueSum(board: number[][]): number {
    let m = board.length, n = board[0].length;
    let arr: [number, number][] = [];
    for(let i = 0; i < m; i++) {
        for(let j = 0; j < n; j++) {
            arr.push([i, j]);
        }
    }
    arr.sort((a, b) => board[b[0]][b[1]] - board[a[0]][a[1]]);
    let vis = new Set(), vis2 = new Set();
    let ans = -Infinity;
    for(let i = 0; i < m*n-2; i++) {
        vis.clear();
        vis2.clear();
        vis.add(arr[i][0]);
        vis2.add(arr[i][1]);
        let sum = board[arr[i][0]][arr[i][1]];
        if(sum*3 <= ans) break;
        for(let j = i+1; j < m*n-1; j++) {
            if(vis.has(arr[j][0]) || vis2.has(arr[j][1])) continue;
            vis.add(arr[j][0]);
            vis2.add(arr[j][1]);
            let x = board[arr[j][0]][arr[j][1]];
            if(sum+x*2 >= ans) {
                sum += x;
                for(let k = j+1; k < m*n; k++) {
                    if(vis.has(arr[k][0]) || vis2.has(arr[k][1])) continue;
                    let y = board[arr[k][0]][arr[k][1]];

                    sum += y;

                    if(sum <= ans) {
                        sum -= y;
                        break;
                    }
                    ans = sum;
                    sum -= y;
                }
            } else {
                sum -= x;
                break;
            }
            sum -= x;
            vis.delete(arr[j][0]);
            vis2.delete(arr[j][1]);
        }
    }
    return ans;
};
