class Solution {
public:
       struct X
    {
        long long val ;
        int x ;
        int y;
    };

  static bool comp(struct X &a , struct X  &b)
   {
    return a.val > b.val;
   }

    long long maximumValueSum(vector<vector<int>>& board) {

        int n=board.size();
        int m=board[0].size();

        X ar[m*n];
        int cnt=0;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
              ar[cnt++]={board[i][j], i ,j};
            }
        }

        sort(ar , ar+ n*m , comp);

        long long ans = -1e15;

        for(int i=0;i<min(n*m ,50) ;i++)
        {
            for(int j=i+1 ;j<min(n*m ,500) ;j++)
            {
                if(ar[i].x == ar[j].x || ar[i].y==ar[j].y) continue;

                for(int k=j+1;k<min(n*m ,1000) ;k++)
                {
                    if(ar[k].x==ar[i].x || ar[k].x==ar[j].x) continue;
                     if(ar[k].y==ar[i].y || ar[k].y==ar[j].y) continue;

                     ans=max(ans,ar[i].val+ar[j].val + ar[k].val);
                }
            }
        }

        return ans;
    }
};
