class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n,m = len(board),len(board[0])

        # dp[t] = heap，最小堆。选了t个的最大值
        dp = [[] for _ in range(4)]
        # 一个数没选
        dp[0].append((0,[]))

        from copy import deepcopy
        MAX_NUM = [1,9,12,1]
        for i in range(n):
            ndp = deepcopy(dp)
            for j in range(m):
                num = board[i][j]
                for t in range(1,4):
                    for lnum,ban in dp[t-1]:
                        if j in ban:
                            continue
                        heappush(ndp[t],(lnum+num,ban+[j]))
                        if len(ndp[t]) > MAX_NUM[t]:
                            heappop(ndp[t])


            dp = ndp
            # for t in range(4):
