class Solution {
public:

    long long maximumValueSum(vector<vector<int>>& board) {

        int m = board.size(), n = board[0].size();
        vector<vector<pair<int, int>>> v(m);
        for(int i = 0; i < m; i++){
            for(int j = 0; j < n; j++){
                v[i].push_back({board[i][j], j});
            }
            sort(v[i].begin(), v[i].end(), greater<pair<int, int>>());
        }


        long long ret = -3 * 1e9;
        for(int i = 0; i < m; i++){

            int B = - 1e9; int idxJ = 0, rJ = 0;
            for(int j = 0, idx = 0; j < m; j++, idx = 0){
                if(i == j)  continue;
                while(v[j][idx].second == v[i][0].second)   idx++;
                if(v[j][idx].first > B){
                    B = v[j][idx].first;
                    rJ = j;  
                    idxJ = idx;
                }
            }

            int C = - 1e9; int idxK = 0, rK = 0;
            for(int k = 0, idx = 0; k < m; k++, idx = 0){
                if(i == k || rJ == k)    continue;
                while(v[k][idx].second == v[i][0].second || v[k][idx].second == v[rJ][idxJ].second)   idx++;
                if(v[k][idx].first > C){
                    C = v[k][idx].first;
                    rK = k;
                    idxK= idx;
                }
            }

            ret = max(ret, (long long)v[i][0].first + v[rJ][idxJ].first + v[rK][idxK].first);

            int B2 = - 1e9; int idxJ2 = 0, rJ2 = 0;
            for(int j = 0, idx = 0; j < m; j++, idx = 0){
                if(i == j || j == rJ)  continue;
                while(v[j][idx].second == v[i][0].second)   idx++;
                if(v[j][idx].first > B2){
                    B2 = v[j][idx].first;
                    rJ2 = j;   
                    idxJ2 = idx; 
                }
            }

            int C2 = - 1e9; int idxK2 = 0, rK2 = 0;
            for(int k = 0, idx = 0; k < m; k++, idx = 0){
                if(i == k || rJ2 == k)    continue;
                while(v[k][idx].second == v[i][0].second || v[k][idx].second == v[rJ2][idxJ2].second)   idx++;
                if(v[k][idx].first > C2){
                    C2 = v[k][idx].first;
                    rK2 = k;   
                    idxK2 = idx;
                }
            }

            ret = max(ret, (long long)v[i][0].first + v[rJ2][idxJ2].first + v[rK2][idxK2].first);



        }

        return ret;

    }
};
