class Solution {
    Map<Integer,Map<Integer,Boolean>> map = new HashMap<>();
    private boolean fun(int arr1,int arr2,int m){
        if(map.containsKey(arr1)){
            if(map.get(arr1).containsKey(arr2)){
                return map.get(arr1).get(arr2);
            }
        }
        int x1 = arr1/m;
        int y1 = arr1%m;
        int x2 = arr2/m;
        int y2 = arr2%m;
        boolean ans = true;
        if(x1==x2||y1==y2){
            ans = false;
        }
        if(!map.containsKey(arr1)){
            map.put(arr1,new HashMap<>());
        }
        map.get(arr1).put(arr2,ans);
        return ans;
    }
    public long maximumValueSum(int[][] board) {
        long ans = Long.MIN_VALUE;
        List<int[]> list = new ArrayList<>();
        int n = board.length;
        int m = board[0].length;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                list.add(new int[]{board[i][j],i*m+j});
            }
        }
        Collections.sort(list, new Comparator<int[]>() {
            @Override
            public int compare(int[] o1, int[] o2) {
                return o2[0]-o1[0];
            }
        });
        int length = list.size();
        for(int i=0;i<length;i++){
            if(ans>= 3L *list.get(i)[0]){
                break;
            }
            for(int j=i+1;j<length;j++){
                for(int k=j+1;k<length;k++){
                    boolean flag = fun(list.get(i)[1],list.get(j)[1],m);
                    flag = flag && fun(list.get(i)[1],list.get(k)[1],m);
                    flag = flag && fun(list.get(j)[1],list.get(k)[1],m);
                    long value = (long) list.get(i)[0] + list.get(j)[0] + list.get(k)[0];
                    if(flag){
                        ans = Math.max(value,ans);
                        break;
                    }
                    if(ans>=value){
                        break;
                    }
                }
                // if(ans>=list.get(i)[0] + list.get(j)[0] + list.get(j)[0]){
                //     break;
                // }
            }
        }
        return ans;
    }
}
