class Solution {
public:
    #define iii pair<long long,pair<long long,long long>>
    #define st first
    #define nd second

    vector<int> ds[501];
    long long ans,D[501],C[501];
    iii B[501]; 
    priority_queue<iii> pq;
    bool check(int a,int b,int c){
        return  B[a].nd.st!= B[b].nd.st && B[c].nd.st!=B[b].nd.st && B[a].nd.st!=B[c].nd.st;
    }
    bool check2(int a,int b,int c){
        return  B[a].nd.nd!= B[b].nd.nd && B[c].nd.nd!=B[b].nd.nd && B[a].nd.nd!=B[c].nd.nd;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                pq.push({board[i][j],{i,j}});
            }
        } 
        int cnt=0;
        while(!pq.empty()){
            iii u=pq.top();
            pq.pop();
           // cout<<u.st<<' ';
            if(C[u.nd.st]<=2 && D[u.nd.nd]<=2){
                B[cnt++]=u;
                C[u.nd.st]++;
                D[u.nd.nd]++;
            }
            if(cnt==200) break;
        }     
        ans=-1e10;
        for(int i=0; i<cnt; i++){
            for(int j=i+1; j<cnt; j++){
                for(int z=j+1; z<cnt; z++){
                    if(check(i,j,z) && check2(i,j,z)){
                       // cout<<B[i].nd.st<<' '<<B[i].nd.nd<<'\n';
                        //cout<<B[i].st<<' '<<B[j].st<<' '<<B[z].st;
                        ans=max(ans,B[i].st+B[j].st+B[z].st);
                    }
                }
            }
        }
        return ans;
    }
};
