class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);

        vector<pair<int, pair<int, int>>> arr;
        int n = board.size(), m = board[0].size();

        for(int i = 0; i < n; i++)
            for(int j = 0; j < m; j++)  
                arr.push_back({board[i][j], {i, j}});

        sort(arr.begin(), arr.end(), greater<pair<int, pair<int, int>>>());

        vector<bool> row(n, false), col(m, false);
        long long mx = LLONG_MIN;

        int limit = min((int)arr.size(), 500);

        for(int i = 0; i < limit; i++) {
            int r1 = arr[i].second.first;
            int c1 = arr[i].second.second;

            if (row[r1] || col[c1]) continue;
            row[r1] = col[c1] = true;

            for(int j = i + 1; j < limit; j++) {
                int r2 = arr[j].second.first;
                int c2 = arr[j].second.second;

                if (row[r2] || col[c2]) continue;
                row[r2] = col[c2] = true;

                for(int k = j + 1; k < limit; k++) {
                    int r3 = arr[k].second.first;
                    int c3 = arr[k].second.second;

                    if (row[r3] || col[c3]) continue;

                    long long currentSum = static_cast<long long>(arr[i].first) +
                                           static_cast<long long>(arr[j].first) +
                                           static_cast<long long>(arr[k].first);
                    mx = max(mx, currentSum);

                    break;
                }

                row[r2] = col[c2] = false;
            }

            row[r1] = col[c1] = false;
        }

        return mx;
    }
};
