#define PII pair<int, int>
class Solution {
long long func(vector<vector<PII>> &pq, int r, int c) {
    int m = pq.size();

    long long ans=LLONG_MIN;
    for(int i=0;i<m;i++) {
        for(int j=i+1;j<m;j++) {
            if(i==c || j==c) {
                continue;
            }

            for(int ii=0;ii<3;ii++) {
                for(int jj=0;jj<3;jj++) {
                    if(pq[i][ii].second == pq[j][jj].second || pq[i][ii].second == r || pq[j][jj].second==r) {
                        continue;
                    }
                    ans = max(ans, 1ll* pq[i][ii].first + pq[j][jj].first);
                }
            }
        }
    } 
    return ans;
}
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();

        vector<vector<int>> board1(m, vector<int>(n));
        if(n<m) { 
            for(int i=0;i<n;i++) {
                for(int j=0;j<m;j++) {
                    board1[j][i] = board[i][j];
                }
            }
            swap(n,m);
            board = board1;
        }



        vector<vector<PII>> pp(m, vector<PII>(3));
        for(int i=0;i<m;i++) {
            priority_queue<PII> pq;
            for(int j=0;j<n;j++) {
                pq.push({board[j][i], j});
            }
            for(int j=0;j<3;j++) {
                pp[i][j] = pq.top();
                pq.pop();
            }
        }

        long long ans = LLONG_MIN;
        for(int i=0;i<m;i++) {
            for(int j=0;j<1;j++) {
                ans = max(ans, func(pp, pp[i][j].second, i) + 1ll*board[pp[i][j].second][i]);
            }
        }
        return ans;
    }
};
