struct s{
    int i;
    int j;
    long long v;
    s(int i,int j,int v):i(i),j(j),v(v){}
    bool operator<(const s& n) const{return this->v<n.v;}
    bool operator>(const s& n) const{return this->v>n.v;}
};
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long max=-4e9,temp;
        int i,j,k;
        int m=board.size();
        int n=board[0].size();
        int l=m<n?3*m+2:3*n+2;
        priority_queue<s,vector<s>,greater<s>> tt;
        for (i=0;i<m;i++){
            for (j=0;j<n;j++){
                if (i*n+j<l)
                    tt.push(s(i,j,board[i][j]));
                else if (board[i][j]>tt.top().v){
                    tt.pop();
                    tt.push(s(i,j,board[i][j]));
                }
            }
        }
        vector<s> t;
        for (i=0;i<l;i++){
            t.push_back(tt.top());
            tt.pop();
        }
        for (int i=0;i<l-2;i++){
            for (int j=i+1;j<l-1;j++){
                if (t[i].i!=t[j].i&&t[i].j!=t[j].j)
                    for (k=j+1;k<l;k++){
                        if (t[k].i!=t[i].i && t[k].i!=t[j].i && t[k].j!=t[i].j && t[k].j!=t[j].j){
                            temp=t[i].v+t[j].v+t[k].v;
                            if (temp>max) max=temp;
                        }
                    }
            }
        }
        return max;
    }
};
