class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        ans = board[0][0] + board[1][1] + board[2][2]
        values = []
        for i, row in enumerate(board):
            for j, val in enumerate(row):
                values.append((val, i, j))
        values.sort(reverse = True)
        n = len(values)
        for i in range(n - 2):
            v0, x0, y0 = values[i]
            if v0 + values[i + 1][0] + values[i + 2][0] <= ans:
                break
            for j in range(i + 1, n - 1):
                v1, x1, y1 = values[j]
                if v0 + v1 + values[j + 1][0] <= ans:
                    break
                elif x0 == x1 or y0 == y1:
                    continue
                for k in range(j + 1, n):
                    v2, x2, y2 = values[k]
                    if v0 + v1 + v2 <= ans:
                        break
                    elif x0 == x2 or x1 == x2 or y0 == y2 or y1 == y2:
                        continue
                    else:
                        ans = v0 + v1 + v2
        return ans
