struct Point{
    long long val, l, r;
};

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<pair<long long, int>>> m;
        int n = board.size();
        int end = board[0].size();

        for (int i = 0; i < n; ++i) {
            vector<pair<long long, int>> topThree;
            for (int j = 0; j < end; ++j) {
                topThree.push_back({board[i][j], j});
            }
            sort(topThree.begin(), topThree.end(), [](const pair<long long, int>& a, const pair<long long, int>& b) {
                return b.first < a.first;
            });
            topThree.resize(3);
            m.push_back(topThree);
        }

        vector<tuple<int, int, int, int, long>> list;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                for (int s = 0; s < 3; s++) {
                    for (int t = 0; t < 3; t++) {
                        if (m[i][s].second == m[j][t].second) continue;
                        list.push_back(make_tuple(i, j, m[i][s].second, m[j][t].second, m[i][s].first + m[j][t].first));
                    }
                }
            }
        }

        sort(list.begin(), list.end(), [](const tuple<int, int, int, int, long>& A, const tuple<int, int, int, int, long>& B) {
            return get<4>(B) < get<4>(A);
        });

        long long ans = -10000000000000;

        for (int i = 0; i < n; i++) {
            bool flag = false;
            for (const auto& v : list) {
                for (int k = 0; k < 3; k++) {
                    long v0 = m[i][k].first;
                    int j0 = m[i][k].second;
                    if (i == get<0>(v) || i == get<1>(v) || j0 == get<2>(v) || j0 == get<3>(v)) continue;
                    if(ans < v0 + get<4>(v)){
                        ans = v0 + get<4>(v); 
                    }
                    flag = true;
                }
                if (flag) break;
            }
        }

        return ans;
    }
};
