using ll = long long;
class Solution {
public:


    long long maximumValueSum(vector<vector<int>>& board) {
        ll n = board.size();
        ll m = board[0].size();
        vector<pair<int, int>> v2;
        for (int i = 0; i < n; i += 1) {
            for (int j = 0; j < m; j += 1) {
                v2.push_back({board[i][j], i*m+j});
            }
        }
        sort(begin(v2), end(v2));
        reverse(begin(v2), end(v2));

        map<int, int> rowcnt, colcnt;

        vector<pair<int, int>> v;
        for (auto &e : v2) {
            int i = e.second/m, j = e.second%m;
            rowcnt[i]++;
            colcnt[j]++;
            if (rowcnt[i] > 6 || colcnt[j] > 6) {
                continue;
            }
            if (v.size() > 36) {
                break;
            }
            v.push_back(e);
        }

        ll mx = v.size();
        // cerr << mx << "?\n";
        ll ans = -3000000000;
        for (int i = 0; i < mx; i += 1) {
            for (int j = i+1; j < mx; j += 1) {
                for (int k = j+1; k < mx; k += 1) {

                    int a = v[i].second/m;
                    int b = v[i].second%m;
                    int c = v[j].second/m;
                    int d = v[j].second%m;
                    int e = v[k].second/m;
                    int f = v[k].second%m;
                    if (a != c &&  a != e && c != e && b != d && b != f && d != f) {
                        ans= max(ans, (ll)v[i].first + (ll)v[j].first + (ll)v[k].first);
                    }
                }
            }
        }
        return ans;
    }
};
