class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(),m=board[0].size();
        vector<vector<pair<int,int>>> mx(m);
        for(int i=0;i<m;i++){
            vector<pair<int,int>> vp;
            for(int j=0;j<n;j++){
                vp.push_back({board[j][i],j});
            }
            sort(vp.rbegin(),vp.rend());
            mx[i].push_back(vp[0]);
            mx[i].push_back(vp[1]);
            mx[i].push_back(vp[2]);
        }
       long long ans=-1e15;
       for(int i=0;i<m;i++){
        for(int j=i+1;j<m;j++){
            for(int k=j+1;k<m;k++){
                long long x;
               for(auto ii: mx[i]){
                for(auto jj: mx[j]){
                    for(auto kk: mx[k]){
                        if(ii.second==jj.second || ii.second==kk.second || jj.second==kk.second)continue;
                        else{
                            x= ii.first; x+=jj.first; x+=kk.first;
                            ans=max(ans,x);
                        }
                    }
                }
               }
            }
        }
       }
        return ans;
    }
};
