class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long m = board.size();
        long long n = board[0].size();
        long long sum = LLONG_MIN;
        long long ans = LLONG_MIN;

        vector<vector<pair<long long, long long>>> memo(board.size());

        for (long long i = 0; i < board.size(); i++) {
            for (long long j = 0; j < board[0].size(); j++) {
                memo[i].push_back({ board[i][j], j });
            }

            sort(memo[i].begin(), memo[i].end());
        }

        for (long long i = 0; i < board.size(); i++) {
            for (long long j = i + 1; j < board.size(); j++) {
                for (long long k = j + 1; k < board.size(); k++) {
                    if (memo[i][n - 1].second != memo[j][n - 1].second && memo[i][n - 1].second != memo[k][n - 1].second && memo[j][n - 1].second != memo[k][n - 1].second) {
                        sum = memo[i][n - 1].first + memo[j][n - 1].first + memo[k][n - 1].first;
                    }
                    else {
                        long long tempSum = LLONG_MIN;
                        for (long long x = 1; x <= 3; x++) {
                            for (long long y = 1; y <= 3; y++) {
                                if (memo[i][n - x].second == memo[j][n - y].second) continue;
                                for (long long z = 1; z <= 3; z++) {
                                    if (memo[i][n - x].second == memo[k][n - z].second || memo[j][n - y].second == memo[k][n - z].second) continue;

                                    tempSum = memo[i][n - x].first + memo[j][n - y].first + memo[k][n - z].first;
                                    if (tempSum > sum) sum = tempSum;
                                    tempSum = LLONG_MIN;
                                }
                            }
                        }
                    }

                    if (sum > ans) ans = sum;
                    sum = LLONG_MIN;
                }
            }
        }

        return ans;
    }
};
