class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e10;
        int n=board.size(),m=board[0].size();
        vector<vector<pair<int,int>>>v(n);
        vector<pair<int,int>>qaq;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                v[i].push_back({board[i][j],j});
                qaq.push_back({board[i][j],i});
            }
            sort(v[i].begin(),v[i].end(),greater<>());
        }
        sort(qaq.begin(),qaq.end(),greater<>());
        unordered_set<int>st;
        for(auto&[x,y]:qaq){
            st.insert(y);
            if(st.size()==3)break;
        }
        for(auto i:st){
            // cout<<i<<" ";
            for(int j=0;j<n;j++){
                if(j==i)continue;
                for(int k=j+1;k<n;k++){
                    if(i==k)continue;
                    for(int x1=0;x1<3;x1++)for(int x2=0;x2<3;x2++)for(int x3=0;x3<3;x3++){
                        if(v[i][x1].second==v[j][x2].second||v[k][x3].second==v[j][x2].second||v[i][x1].second==v[k][x3].second)continue;
                        ans=max(0ll+v[i][x1].first+v[j][x2].first+v[k][x3].first,ans);
                        // cout<<i<<" "<<j<<" "<<k<<endl;
                    }
                }
            }
        }
        return ans;
    }
};
