class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        long long ans = -4e9;
        int n = a.size(), m = a[0].size();
        vector<pair<long long, pair<int, int>>> values;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                values.push_back({a[i][j], {i, j}});
            }
        }   
        sort(values.rbegin(), values.rend());
        for(int i = 0; i < values.size(); i++){
            int v = values[i].first;
            auto [r, c] = values[i].second;
            auto solve = [&](int start){
                long long temp = v * 1LL;
                vector<int> row, col;
                row.push_back(r);
                col.push_back(c);
                for(int j = start; j < values.size(); j++){
                    bool rowOk = true, colOk = true;
                    int v1 = values[j].first;
                    auto [r1, c1] = values[j].second;
                    for(auto rr : row) if(rr == r1) rowOk = false;
                    for(auto cc : col) if(cc == c1) colOk = false;
                    if(rowOk && colOk) {
                        temp += (v1 * 1LL);
                        row.push_back(r1);
                        col.push_back(c1);
                    }
                    if(row.size() == 3) break;
                }
                if(row.size() == 3) return temp;
                else return (long long)-4e9;
            };  
            ans = max(ans, solve(i+1));
            ans = max(ans, solve(0));
        }
        return ans; 
    }
};

