class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        arr = []
        ans = -1 << 60
        r = []
        for i in range(m):
            r.append([])
            for j in range(n):
                r[-1].append([board[i][j], i, j])
            r[-1].sort(reverse=True)
            r[-1] = r[-1][:3]

        mx = []
        for j in range(3):
            t = []
            for i in range(m):
                t.append(r[i][j])
            mx.append(sorted(t, reverse=True)[:3])

        for i in range(m):
            for v1, x1, y1 in r[i]:
                for j in range(i+1, m):
                    for v2, x2, y2 in r[j]:
                        if x1 != x2 and y1 != y2:
                            for k in range(3):
                                for v3, x3, y3 in mx[k]:
                                    if x1 != x3 and x2 != x3 and y1 != y3 and y2 != y3:
                                        ans = max(ans, v1 + v2 + v3)
        return ans
