class Solution {
    public long maximumValueSum(int[][] board) {
        List<node> list = new ArrayList<>();
        for (int i = 0; i < board.length; i++) {
            for (int j = 0; j < board[i].length; j++) {
                list.add(new node(board[i][j], i, j));
            }
        }
        Collections.sort(list, Comparator.comparing(n -> n.value));
        Collections.reverse(list);
        long ans = Long.MIN_VALUE;
        for (int i = 0; i < board.length; i++) {
            for (int j = 0; j < board[i].length; j++) {
                for (int u = 0; u < list.size() && u < 100; u++) {
                    node n = list.get(u);
                    if (n.row != i && n.colum != j) {
                        long sum  =  board[i][j] +n.value;
                        int ii = n.row;
                        int jj = n.colum;
                        for (int uu = u + 1; uu < list.size() && uu < 100; uu++) {
                            node nn = list.get(uu);
                            if (nn.row != i && nn.colum != j && nn.row != ii && nn.colum != jj) {
                                sum += nn.value;
                                ans = Long.max(ans, sum);
                                break;
                            }
                        }
                    }
                }
            }


        }
        return ans;

    }

    public static class node {
        public int value;
        public int row;
        public int colum;

        node(int value, int row, int colum) {
            this.value = value;
            this.row = row;
            this.colum = colum;
        }

        public int getValue() {
            return this.value;
        }


    }
}
