class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int x=board.size();
        int y=board[0].size();
        vector<long long> temp(3,0);
        vector<vector<long long>> st(board.size()*board[0].size(),temp);
        int cnt=0;
        for(int i=0;i<x;i++){
            for(int j=0;j<y;j++){
                // cout<<i<<" "<<j<<" "<<i*x+j<<endl;
                st[cnt][0]=board[i][j];
                st[cnt][1]=i;
                st[cnt][2]=j;
                cnt++;
            }
        }
        // cout<<endl;
        sort(st.begin(),st.end());
        // for(int i=0;i<st.size();i++){
        //     cout<<i<<" "<<st[i][0]<<" "<<st[i][1]<<" "<<st[i][2]<<endl;
        // }
        long long maxm=LLONG_MIN;
        // cout<<endl;
        for(int i=st.size()-1;i>=0;i--){
            if(i>2 && st[i][0]+st[i-1][0]+st[i-2][0]<maxm){
                return maxm;
            }
            for(int j=i-1;j>=0;j--){
                for(int k=j-1;k>=0;k--){
                    if(st[i][0]+st[j][0]+st[k][0]>maxm){
                        if(st[i][1]!=st[j][1] &&st[j][1]!=st[k][1] && st[k][1]!=st[i][1]){
                            if(st[i][2]!=st[j][2] &&st[j][2]!=st[k][2] && st[k][2]!=st[i][2]){
                                // cout<<i<<" "<<j<<" "<<k<<" "<<st[i][0]+st[j][0]+st[k][0]<<endl;
                                maxm=st[i][0]+st[j][0]+st[k][0];
                            }
                        }
                    }
                    else{
                        break;
                    }
                }
            }
        }
        return maxm;
    }
};
