class node{
public:
    int x,y;
    long long v;
    friend bool operator<(node &a,node &b){
        return a.v>b.v;
    }
};
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size();
        int n=board[0].size();
        int maxn=board[0][0];
        vector<node> k(m*n);
        int ji=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                k[ji++]={i,j,board[i][j]};
                maxn=max(maxn,board[i][j]);
            }
        }
        sort(k.begin(),k.end());
        long long ans=-0x7f7f7f7f7f7f7f7f;
        for(int i=0;i<m*n;i++){
            if((k[i].v+2*maxn)<ans){
                break;
            }
            for(int j=i+1;j<m*n;j++){
                if((k[i].v+k[j].v+maxn)<ans){
                    break;
                }
                for(int p=j+1;p<m*n;p++){
                    if((k[i].v+k[j].v+k[p].v)<ans){
                        break;
                    }
                    if(k[i].x!=k[j].x&&k[j].x!=k[p].x&&k[i].x!=k[p].x){
                        if(k[i].y!=k[j].y&&k[j].y!=k[p].y&&k[i].y!=k[p].y){
                            ans=max(ans,k[i].v+k[j].v+k[p].v);
                        }
                    }
                }
            }
        }
        return ans;
    };
};
