class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        l = []
        for i, r in enumerate(board):
            for j, v in enumerate(r):
                l.append((v,i,j))
        l.sort(reverse=True)
        nl = []
        si = Counter()
        sj = Counter()
        li = lj = 0
        for v, i, j in l:
            if (li < 5 and i not in si) or (i in si and si[i] < 3):
                nl.append((v, i, j))
                si[i] += 1
                sj[j] += 1
                li = len(si)
                lj = len(sj)
            elif (lj < 5 and j not in sj) or (j in sj and sj[j] < 3):
                nl.append((v, i, j))
                si[i] += 1
                sj[j] += 1
                li = len(si)
                lj = len(sj)
            if len(nl) == 15:
                break

        res = -int(5e9)
        tl = nl
        # print(len(tl))
        # print(tl)
        for i in range(len(tl)):
            a, ai, aj = tl[i]
            for j in range(i + 1, len(tl)):
                b, bi, bj = tl[j]
                if bi == ai or bj == aj:
                    continue
                for k in range(j + 1, len(tl)):
                    c, ci, cj = tl[k]
                    if ai != ci and bi != ci and cj != aj and cj != bj:
                        res = max(res, a + b + c)

        return res
