#define ll long long
class Solution {
public:

    long long func(vector<vector<ll>>&a){
        ll ans = 0;
        bool init = false;
        int n = a.size();
        for(int i = 0;i<n;i++){

            for(int j = i+1;j<n;j++){
                if(a[i][1] == a[j][1] || a[i][2] == a[j][2])    continue;

                for(int k = j+1;k<n;k++){

                    if(a[i][1] != a[j][1] && a[j][1] != a[k][1] && a[i][1] != a[k][1]){
                      if(a[i][2] != a[j][2] && a[j][2] != a[k][2] && a[i][2] != a[k][2]){

                          if(init == false){
                              ans = a[i][0] + a[j][0] + a[k][0];
                              init = true;
                          }
                          else            ans = max(ans,a[i][0] + a[j][0] + a[k][0]);
                      }
                    }
                }
            }
        }
        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& v) {



        int m = v.size(),n=v[0].size();
        vector<vector<ll>>a;

        for(int i = 0;i<m;i++){
            for(int j = 0;j<n;j++)          a.push_back({v[i][j],i,j});
        }

        sort(a.begin(),a.end());
        reverse(a.begin(),a.end());

        if(m <= 9 && n <= 9 && m*n <= 100)       return func(a);

        vector<int>row(m,0),col(n,0);

        int rC = 0,cC = 0;
        int i;
        for(i=0;i<a.size();i++){
            row[a[i][1]]++;
            col[a[i][2]]++;
            if(row[a[i][1]] == 1)       rC++;
            if(col[a[i][2]] == 1)       cC++;
            if(rC >= min(9,m) && cC >= min(9,n))      break;
        }
        n = i+1;
        a.resize(n);
        // for(int i = 0;i<n;i++)      cout<<" - "<<a[i][0]<<" "<<a[i][1]<<" "<<a[i][2]<<" - ";

        return func(a);
        // return ans;

    }
};
