class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int nRow = board.size();
        int nCol = board[0].size();
        vector<long long> maxSuf = vector<long long>();
        vector<vector<pair<int, int> > > b = vector<vector<pair<int, int> > >();

        for (int x = 0; x < nRow; x++) {
            b.push_back(vector<pair<int, int> >());
            for (int y = 0; y < nCol; y++) {
                b[x].push_back({board[x][y], y});
            }
        }
        for (int x = 0; x < nRow; x++) {
            sort(b[x].begin(), b[x].end(), greater<pair<int, int> >());
        }
        for (int x = 0; x < nRow; x++) {
            maxSuf.push_back(b[x][0].first);
        }
        for (int x = nRow - 2; x >= 0; x--) {
            maxSuf[x] = max(maxSuf[x], maxSuf[x + 1]);
        }
        long long res = -100000000000;

        for (int x = 0; x < nRow - 2; x++) {
            if (b[x][0].first + maxSuf[x + 1] + maxSuf[x + 2] <= res) continue;
            for (int y = x + 1; y < nRow - 1; y++) {
                if (b[x][0].first + b[y][0].first + maxSuf[y + 1] <= res) continue;
                for (int z = y + 1; z < nRow; z++) {
                    if (0ll + b[x][0].first + b[y][0].first + b[z][0].first <= res) continue;
                    for (int ix = 0; ix < 3; ix++) {
                        for (int iy = 0; iy < 3; iy++) {
                            if (b[x][ix].second == b[y][iy].second) continue;
                            for (int iz = 0; iz < 3; iz++) {
                                if (b[x][ix].second == b[z][iz].second) continue;
                                if (b[y][iy].second == b[z][iz].second) continue;
                                res = max(res, 0ll + b[x][ix].first + b[y][iy].first + b[z][iz].first);
                                break;
                            }

                        }
                    }
                }
            }
        }
        return res;
    }
};
