class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        col_max = [[(-1, -inf)]*3 for _ in range(n)]
        a = []
        for j, col in enumerate(zip(*board)):
            for i, x in enumerate(col):
                a.append((x, i, j))
                if x >= col_max[j][0][1]:
                    col_max[j][1], col_max[j][2] = col_max[j][0], col_max[j][1]
                    col_max[j][0] = (i, x)
                elif x >= col_max[j][1][1]:
                    col_max[j][2] = col_max[j][1]
                    col_max[j][1] = (i, x)
                elif x > col_max[j][2][1]:
                    col_max[j][2] = (i, x)

        ans = -inf
        a.sort(reverse = True)
        # 枚举选择的列
        for j1 in range(n - 2):
            for j2 in range(j1 + 1, n - 1):
                for i1, x1 in col_max[j1]:
                    for i2, x2 in col_max[j2]:
                        if i1 == i2:
                            continue
                        for x3, i3, j3 in a:
                            if i3 == i1 or i3 == i2 or j3 == j1 or j3 == j2:
                                continue
                            if x1 + x2 + x3 > ans:
                                ans = x1 + x2 + x3
                            break
        return ans
