#define PI 3.141592653589793238462
#define set_bits(x) __builtin_popcountll(x)
#define all(v) sort(v.begin(),v.end())
#define rall(v) sort(v.rbegin(),v.rend())
#define ar(a) a.begin(),a.end()
#define bs binary_search
#define lb lower_bound
#define ub upper_bound
#define rev(a) reverse(a.begin(),a.end())
#define f0(i,a,n) for(ll i=a;i<n;i++)
#define f1(i,b,a) for(ll i=b;i>=a;i--)
#define bbl(a) __builtin_popcountll(a)
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ll m = board.size();
        ll n = board[0].size();
        vector<pair<ll, pair<ll, ll>>> candidates;

        f0(i, 0, m) {
            f0(j, 0, n) {
                candidates.push_back({(ll)board[i][j], {i, j}});
            }
        }

        sort(candidates.rbegin(), candidates.rend());

        ll maxSum = -5e15; 

        ll len = min((ll)400, (ll)candidates.size());

        f0(a, 0, len) {
            f0(b, a + 1, len) {
                f0(c, b + 1, len) {
                    ll v1 = candidates[a].first;
                    ll r1 = candidates[a].second.first;
                    ll c1 = candidates[a].second.second;

                    ll v2 = candidates[b].first;
                    ll r2 = candidates[b].second.first;
                    ll c2 = candidates[b].second.second;

                    ll v3 = candidates[c].first;
                    ll r3 = candidates[c].second.first;
                    ll c3 = candidates[c].second.second;

                    if (r1 != r2 && r1 != r3 && r2 != r3 && c1 != c2 && c1 != c3 && c2 != c3) {
                        ll candidateSum = v1 + v2 + v3;
                        maxSum = max(maxSum, candidateSum);
                    }
                }
            }
        }

        return maxSum;
    }
};
