class Solution {
public:
vector<pair<int,pair<int,int>>>vv;
    long long maximumValueSum(vector<vector<int>>& board) {

        for(int i=0;i<board.size();i++){
            for(int j=0;j<board[i].size();j++){
                vv.push_back({-board[i][j],{i,j}});
            }
        }
        sort(vv.begin(),vv.end());
       vector<pair<int,pair<int,int>>>nn;
       long long maxx=-1e18;
       int duo=100;
       for(auto it:vv){
        if(nn.size()>410)break;
        nn.push_back({-it.first,it.second});
       }
       for(int i=0;i<nn.size();i++){
        for(int j=i+1;j<nn.size();j++){
            for(int k=j+1;k<nn.size();k++){
                if(nn[i].second.first==nn[j].second.first)continue;
                 if(nn[j].second.first==nn[k].second.first)continue;
                 if(nn[i].second.first==nn[k].second.first)continue;
                  if(nn[i].second.second==nn[j].second.second)continue;
                 if(nn[j].second.second==nn[k].second.second)continue;
                 if(nn[i].second.second==nn[k].second.second)continue;
                 maxx=max(maxx,1ll*(nn[i].first+nn[j].first)+nn[k].first);
            }
        }
       }
       return maxx;
    }
};
