class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        vector<vector<int>>aa;
        int n=b.size();
        int m=b[0].size();
        int lim=3*(n+m);
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                ;
                aa.push_back({b[i][j],i,j});
            }
        }


        sort(aa.rbegin(),aa.rend());
        // reverse(aa.begin(),aa.end());
        // for(int i=0;i<aa.size();i++)
        // {
        //     cout<<aa[i][0]<<" ";
        // }
        // cout<<endl;
        // unordered_set<int>co,ro;
        long long sum=0,ans=-1e18;
        for(int i=0;i<min(lim,(int)aa.size());i++)
        {
            sum+=aa[i][0];
            for(int j=i+1;j<min(lim,(int)aa.size());j++)
            {
                // if(aa[j][1]<aa[i][1])continue;
                if(aa[j][1]==aa[i][1]||aa[j][2]==aa[i][2])continue;

                sum+=aa[j][0];
                for(int k=j+1;k<min(lim,(int)aa.size());k++)
                {
                    // if(aa[k][1]<aa[j][1])continue;
                    if(aa[k][1]==aa[i][1]||aa[k][2]==aa[i][2]||aa[j][1]==aa[k][1]||aa[j][2]==aa[k][2])continue;
                    sum+=aa[k][0];

                    {
                        ans=max(ans,sum);
                    }
                    sum-=aa[k][0];
                    break;
                }
                sum-=aa[j][0];

            }
            sum-=aa[i][0];

        }
        return ans;
    }
};
