class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        arr = []
        n, m = len(board), len(board[0])
        for i in range(n):
            for j in range(m):
                arr.append((board[i][j], i, j))
        arr.sort()
        k = len(arr)
        ans = -inf
        for i in reversed(range(k)):
            if arr[i][0] * 3 <= ans:
                break
            v1, r1, c1 = arr[i]
            for j in reversed(range(i)):
                if arr[j][0] + arr[j][0] + v1 <= ans:
                    break
                if arr[j][1] == r1 or arr[j][2] == c1:
                    continue
                v2, r2, c2 = arr[j]
                for y in reversed(range(j)):
                    if arr[y][0] + v2 + v1 <= ans:
                        break
                    if arr[y][1] == r1 or arr[y][2] == c1 or arr[y][1] == r2 or arr[y][2] == c2:
                        continue
                    ans = max(ans, arr[y][0] + v2 + v1)
        return ans
