class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v) {
        ll n = v.size(), m = v[0].size();

        vvll dp(n);
        vlll m9;
        rep(i,0,n){
            vll t(m);
            rep(j,0,m){
                t[j] = {v[i][j],j};
            }
            sort(all(t));

            rep(j,0,3){
                dp[i].pb(t[m-1-j]);
            }
        }

        rep(i,0,n)
        rep(j,0,m){
            m9.pb({v[i][j],i,j});            
        }
        sort(rall(m9));

        ll a = -LONG_LONG_MAX;

        rep(di1,0,n){
            rep(j1,0,3){
                auto [a1,dj1] = dp[di1][j1];
                rep(di2,0,n){
                    if(di2==di1) continue;
                    rep(j2,0,3){
                        auto [a2,dj2] = dp[di2][j2];
                        if(dj1==dj2) continue;

                        rep(p,0,9){
                            auto[a3, di3, dj3] = m9[p];
                            if(di3 == di1 or di3 == di2 or dj3 == dj1 or dj3 == dj2) continue;
                            a = max(a, a1+a2+a3);
                        }
                    }
                }
            }
        }

        return a;
    }   
};
