class Solution {
    public long maximumValueSum(int[][] board) {
        int n=board.length, m=board[0].length;
        int a[][]=new int[n*m][3];
        int ai=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                a[ai][0]=board[i][j];
                a[ai][1]=i;
                a[ai][2]=j;
                ai++;
            }
        }
        Arrays.sort(a, (mm,nn)->mm[0]-nn[0]);
        long ans=Long.MIN_VALUE;
        for(int tt=m*n-1;tt>=m*n-50 && tt>=0 ;tt--){
            long tans=a[tt][0];
            int i1=a[tt][1], j1=a[tt][2];
            for(int i=m*n-1;i>=0;i--){
                if(a[i][1]!=i1 && a[i][2]!=j1){
                    int i2=a[i][1], j2=a[i][2];
                    for(int j= i-1;j>=0;j--){
                        if(a[j][1]!=i1 && a[j][2]!=j1 && a[j][1]!=i2 && a[j][2]!=j2){
                            ans=(long)Math.max(ans,(long)board[i1][j1]+(long)board[i2][j2]+(long)a[j][0]);
                            break;
                        }
                    }
                }
            }
        }
        return ans;
    }
}
