class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        cands = sorted((-board[i][j], i, j) for i,j in product(range(m), range(n)))[:2*(m+n)-3]
        l, v = len(cands), set([(0,1,2)])
        heap = [(cands[0][0]+cands[1][0]+cands[2][0], 0, 1, 2)]

        while heap:
            res, r,s,t = heappop(heap)

            (_, i1, j1), (_, i2, j2), (_, i3, j3) = cands[r], cands[s], cands[t]

            if len(set([i1,i2,i3])) == len(set([j1,j2,j3])) == 3:
                return -res

            if r+1 < s and (r+1, s, t) not in v:
                v.add((r+1, s, t))
                heappush(heap, (cands[r+1][0]+cands[s][0]+cands[t][0], r+1,s,t))
            if s+1 < t and (r, s+1, t) not in v:
                v.add((r, s+1, t))
                heappush(heap, (cands[r][0]+cands[s+1][0]+cands[t][0], r,s+1,t))
            if t+1 < l and (r, s, t+1) not in v:
                v.add((r, s, t+1))
                heappush(heap, (cands[r][0]+cands[s][0]+cands[t+1][0], r,s,t+1))
        return -1
