#include <bits/stdc++.h>
#define fst first
#define snd second
#define int64 long long
#define FOR(i, a, b) for(int i = a; i <= b; i++)
#define FORD(i, b, a) for(int i = b; i >= a; i--)
using namespace std;

typedef pair<int64,int64> ii;

template<class X, class Y> bool maximize(X &a, Y b)
{
    if(a >= b) return false;
    a = b;
    return true;
}

template<class X, class Y> bool minimize(X &a, Y b)
{
    if(a <= b) return false;
    a = b;
    return true;
}
const int N = 5555;

class Solution {
public:
    vector<ii> mxRows[N];

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size() - 1;
        int m = board[0].size() - 1;

        FOR(c, 0, m)
        {
            set<ii> value;

            FOR(r, 0, n)
            {
                value.insert(ii(board[r][c], r));
            }
            while(mxRows[c].size() < 3)
            {
                mxRows[c].push_back((*value.rbegin()));
                value.erase(*value.rbegin());
            }
        }
        int64 ans = -1e18;
        FOR(c1, 0, m)
        {
            FOR(c2, c1 + 1, m)
            {
                FOR(c3, c2 + 1, m)
                {
                    for(auto r1: mxRows[c1])
                        for(auto r2: mxRows[c2])
                        {
                            if(r1.snd == r2.snd) continue;

                            for(auto r3: mxRows[c3])
                            {
                                if(r2.snd == r3.snd || r1.snd == r3.snd) continue;

                                ans = max(ans, r1.fst + r2.fst + r3.fst);
                            }
                        }
                }
            }
        }
        return ans;
    }
};
