class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        h, w = len(board), len(board[0])
        x2ys = [[] for _ in range(w)]
        for y in range(h):
            max3s = list(sorted((v, x) for x, v in enumerate(board[y])))[-3:]
            for v, x in max3s:
                x2ys[x].append((v, y))
        max9s = []
        for x in range(w):
            x2ys[x].sort(reverse=True)
            if len(x2ys[x]) > 0:
                max9s.append((x2ys[x][0], x))
        max9s.sort(reverse=True)
        if len(max9s) > 9:
            max9s = max9s[:9]
        l = len(max9s)
        def sub2(y1, y2, x3):
            ans = float('-inf')
            for i in range(min(3, len(x2ys[x3]))):
                if x2ys[x3][i][1] != y1 and x2ys[x3][i][1] != y2:
                    ans = max(ans, x2ys[x3][i][0])
            return ans                    

        def sub1(y1, x2, x3):
            ans = float('-inf')
            for i in range(min(3, len(x2ys[x2]))):
                if x2ys[x2][i][1] != y1:
                    ans = max(ans, x2ys[x2][i][0] + sub2(y1, x2ys[x2][i][1], x3))
            return ans                    
        def sub(x1, x2, x3):
            ans = float('-inf')
            for i in range(min(3, len(x2ys[x1]))):
                ans = max(ans, x2ys[x1][i][0] + sub1(x2ys[x1][i][1], x2, x3))
            return ans                
        ans = float('-inf')
        for i1 in range(2, l):
            for i2 in range(1, i1):
                for i3 in range(0, i2):
                    ans = max(ans, sub(max9s[i1][1], max9s[i2][1], max9s[i3][1]))
        return ans
