class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:

        n,m=len(board),len(board[0])

        heap=[]
        for i in range(n):
            for j in range(m):
                heap.append((board[i][j],i,j))

        heap=sorted(heap,reverse=True)

        ans=float("-inf")
        for i in range(n+m):
            v,x,y=heap[i]
            count,j=0,i+1
            while count<=n+m and j<len(heap):
                if x != heap[j][1] and y != heap[j][2]:
                    v2,k,l=heap[j]
                    count+=1
                    t=j+1
                    while t<len(heap):
                        if heap[t][1] not in (x,k) and heap[t][2] not in (y,l):
                            v3,t1,t2=heap[t]
                            ans=max(ans,v+v2+v3)
                            break
                        t+=1

                j+=1

        return ans
