class Solution {
public:
    struct ran{
        int x, y, z;
        bool operator < (const ran &a)const{
            return z > a.z;
        }
    };
    long long maximumValueSum(vector<vector<int>>& tr) {
        int n = tr.size(), m = tr[0].size();
        vector<ran>v;
        for(int i = 0; i < n; ++i){
            for(int j = 0; j < m; ++j){
                v.push_back({i, j, tr[i][j]});
            }
        }
        sort(v.begin(), v.end());
        vector<ran>vv;
        map<int, int>hang, lie;
        for(int i = 0; i < v.size(); ++i){
            if(hang[v[i].x] > 5 || lie[v[i].y] > 5)continue;
            vv.push_back(v[i]);
            ++hang[v[i].x];
            ++lie[v[i].y];
        }

        int len = min(305, (int)vv.size());
        long long ans = -3e9;
        for(int i = 0; i < len; ++i){
            for(int j = i + 1; j < len; ++j){
                for(int k = j + 1; k < len; ++k){
                    if(vv[i].x != vv[j].x && vv[i].x != vv[k].x && vv[j].x != vv[k].x
                    &&vv[i].y != vv[j].y && vv[i].y != vv[k].y && vv[j].y != vv[k].y){
                        ans = max(ans, (long long)vv[i].z + vv[j].z + vv[k].z);
                    }
                }
            }
        }
        return ans;
    }
};
