class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        int64_t ret = LLONG_MIN;
        vector<vector<pair<int64_t, int>>> a(m, vector<pair<int64_t, int>>(n));

        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                a[i][j] = { board[i][j], j }; 
            }
        }
        for (auto &r: a) sort(r.rbegin(), r.rend());

        vector<vector<pair<int64_t, int>>> data(m);
        priority_queue<pair<int64_t, int>> pq; 

        for (int i = m - 1; i >= 0; --i) {
            for (int k = 0; k < 3; ++k) {
                pq.push(a[i][k]);
            }
            vector<int> flag(n);
            while (data[i].size() < 3) {
                if (data[i].empty() or flag[pq.top().second] == 0) {
                    flag[pq.top().second] = 1;
                    data[i].push_back(pq.top());
                }
                pq.pop();
            }
            while (not pq.empty()) pq.pop();
            for (auto &it: data[i]) pq.push(it);
        }

        for (int it1 = 0; it1 < m; ++it1) {
            for (int it2 = it1 + 1; it2 + 1 < m; ++it2) {
                int it3 = it2 + 1;

                for (int k1 = 0; k1 < 3; ++k1) {
                    for (int k2 = 0; k2 < 3; ++k2) {
                        for (int k3 = 0; k3 < 3; ++k3) {

                            if (a[it1][k1].second == a[it2][k2].second) continue;
                            if (a[it2][k2].second == data[it3][k3].second) continue;
                            if (data[it3][k3].second == a[it1][k1].second) continue;

                            ret = max(ret, a[it1][k1].first + a[it2][k2].first + data[it3][k3].first);
                        }
                    }                
                }    
            }
        }

        return ret;
    }
};
