class Solution {
public:
    using ll = long long;
    ll maximumValueSum(vector<vector<int>>& b) {
        int m = b.size();
        int n = b[0].size();
        vector<vector<ll>> p;
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                p.push_back({b[i][j], i, j});
            }
        }
        sort(p.rbegin(), p.rend());
        int sz = p.size();
        // for (int i = 0; i < sz; ++i) {
        //     cout << i << ": " << p[i][0] << " from (" << p[i][1] << ", " << p[i][2] << ")" << endl;
        // }
        vector<bool> r(m, false), c(n, false);
        ll ans = -1'000'000'000LL * 3;

        for (int i = 0; i < sz - 2 && p[i][0]+p[i+1][0]+p[i+2][0] > ans; ++i) {
            int x = p[i][1];
            int y = p[i][2];
            r[x] = true;
            c[y] = true;
            for (int j = i + 1; j < sz - 1 && p[i][0]+p[j][0]+p[j+1][0] > ans; ++j) {
                int x = p[j][1];
                int y = p[j][2];
                if (r[x] || c[y]) continue;
                r[x] = true;
                c[y] = true;
                for (int k = j + 1; k < sz && p[i][0]+p[j][0]+p[k][0] > ans; ++k) {
                    int x = p[k][1];
                    int y = p[k][2];
                    if (r[x] || c[y]) continue;
                    ans = p[i][0]+p[j][0]+p[k][0];
                    // cout << "i = " << i << ", j = " << j << ", k = " << k << endl;
                }
                r[x] = false;
                c[y] = false;
            }
            r[x] = false;
            c[y] = false;
        }
        return ans;
    }
};
