class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size() , n = board[0].size();

        vector<pair<int,pair<int,int>>> vp;
        for(int i=0;i<m;i++){
            vector<pair<int,int>> temp;
            for(int j=0;j<n;j++){
                temp.push_back({board[i][j] , j});
            }
            sort(temp.begin() , temp.end());
            reverse(temp.begin() , temp.end());
            int x = 3;
            x = min(x , (int) temp.size());
            for(int j=0;j<x;j++){
                vp.push_back({temp[j].first , {i , temp[j].second}});
            }
        }

        vector<pair<int,pair<int,int>>> new_vp;
        for(int j=0;j<n;j++){
            vector<pair<int,pair<int,int>>> temp;
            for(int k=0;k<vp.size();k++){
                if(vp[k].second.second == j){
                    temp.push_back(vp[k]);
                }
            }
            sort(temp.begin() , temp.end());
            reverse(temp.begin() , temp.end());
            int x = 3;
            x = min(x , (int) temp.size());
            for(int i=0;i<x;i++){
                new_vp.push_back({temp[i].first , {temp[i].second.first , temp[i].second.second}});
            }
        }

        long long res  = -1e12;

        for(int i=0;i<new_vp.size();i++){
            long long ans = new_vp[i].first;
            int idx1 = new_vp[i].second.first , idx2 = new_vp[i].second.second;
            for(int j=0;j<new_vp.size();j++){
                if(new_vp[j].second.first != idx1 && new_vp[j].second.second != idx2){
                    long long ansHere = ans + new_vp[j].first;
                    for(int k=0;k<new_vp.size();k++){
                        if(new_vp[k].second.first != idx1 && new_vp[k].second.second != idx2
                            && new_vp[k].second.first != new_vp[j].second.first &&
                            new_vp[k].second.second != new_vp[j].second.second
                            ){
                            res = max(res , ansHere + new_vp[k].first); 
                        }
                    }
                }
            }
        }
        return res;
    }
};
