typedef long long ll;

ll pr[500][500];
ll sr[500][500];

ll ul[500][500];
ll ur[500][500];
ll dl[500][500];
ll dr[500][500];


class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& arr) {

        ll n = arr.size();
        ll m = arr[0].size();

        const ll nINF = -1e16;

        ll ans = nINF;

//         vector< vector<ll> > pr( n, vector<ll>( m));
//         vector< vector<ll> > sr( n, vector<ll>( m));

//         vector< vector<ll> > ul( n, vector<ll>( m));
//         vector< vector<ll> > ur( n, vector<ll>( m));
//         vector< vector<ll> > dl( n, vector<ll>( m));
//         vector< vector<ll> > dr( n, vector<ll>( m));


        for( ll i=0; i<n; i++) {
            for( ll j=0; j<m; j++) {
                ul[i][j] = ur[i][j] = dl[i][j] = dr[i][j] = pr[i][j] = sr[i][j] = arr[i][j];
            }
        }

        for( ll i=0; i<n; i++) {
            for( ll j=1; j<m; j++) {
                pr[i][j] = max( pr[i][j], pr[i][j-1]);
            }
        }

        for( ll i=0; i<n; i++) {
            for( ll j=m-2; j>=0; j--) {
                sr[i][j] = max( sr[i][j], sr[i][j+1]);
            }
        }

        for( ll i=0; i<n; i++) {
            for( ll j=0; j<m; j++) {
                ul[i][j] = max(
                    ul[i][j],
                    max( (i>0)?ul[i-1][j]:nINF, (j>0)?ul[i][j-1]:nINF)
                );
            }
        }

        for( ll i=0; i<n; i++) {
            for( ll j=m-1; j>=0; j--) {
                ur[i][j] = max(
                    ur[i][j],
                    max( (i>0)?ur[i-1][j]:nINF, (j+1<m)?ur[i][j+1]:nINF)
                );
            }
        }

        for( ll i=n-1; i>=0; i--) {
            for( ll j=0; j<m; j++) {
                dl[i][j] = max(
                    dl[i][j],
                    max( (i+1<n)?dl[i+1][j]:nINF, (j>0)?dl[i][j-1]:nINF)
                );
            }
        }

        for( ll i=n-1; i>=0; i--) {
            for( ll j=m-1; j>=0; j--) {
                dr[i][j] = max(
                    dr[i][j],
                    max( (i+1<n)?dr[i+1][j]:nINF, (j+1<m)?dr[i][j+1]:nINF)
                );
            }
        }

        for( ll i=0; i<n; i++) {
            for( ll j=0; j<m; j++) {
                if((i>0) && (j>0) && (i+1<n) && (j+1<m)) {
                    ans = max( ans, arr[i][j]+ul[i-1][j-1]+dr[i+1][j+1]);
                    ans = max( ans, arr[i][j]+dl[i+1][j-1]+ur[i-1][j+1]);
                }
                if((j>0) && (j+1<m) && (i+2<n)) {
                    for( ll ii=i+1; ii+1<n; ii++) {
                        ans = max( ans, arr[i][j]+pr[ii][j-1]+dr[ii+1][j+1]);
                        ans = max( ans, arr[i][j]+dl[ii+1][j-1]+sr[ii][j+1]);
                    }   
                }
                if((j>0) && (j+1<m) && (i-2>=0)) {
                    for( ll ii=i-1; ii>0; ii--) {
                        ans = max( ans, arr[i][j]+pr[ii][j-1]+ur[ii-1][j+1]);
                        ans = max( ans, arr[i][j]+ul[ii-1][j-1]+sr[ii][j+1]);
                    }
                }
            }
        }

//         ul.clear();
//         ur.clear();
//         dl.clear();
//         dr.clear();

//         pr.clear();
//         sr.clear();


        return ans;
    }
};
