class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        minsum = points[0][0] + points[0][1]
        maxsum = points[0][0] + points[0][1]
        mindiff = points[0][0] - points[0][1]
        maxdiff = points[0][0] - points[0][1]
        a,b,c,d = 0,0,0,0

        for i in range(1,len(points)):
            sum = points[i][0] + points[i][1]
            diff = points[i][0] - points[i][1]
            if (sum < minsum):
                minsum = sum
                a = i
            elif (sum > maxsum):
                maxsum = sum
                b = i 
            if (diff < mindiff):
                mindiff = diff
                c = i
            elif (diff > maxdiff):
                maxdiff = diff
                d = i 

            # print(maxsum - minsum, maxdiff - mindiff,a,b,c,d)

        maxi = max(maxsum - minsum, maxdiff - mindiff)
        pt = []

        if maxsum - minsum > maxdiff - mindiff :
            pt = [a,b]
        elif maxsum - minsum < maxdiff - mindiff :
            pt = [c,d]
        else:
            pt = [a,b,c,d]

        for x in pt :

            if x==0 :
                minsum = points[1][0] + points[1][1]
                maxsum = points[1][0] + points[1][1]
                mindiff = points[1][0] - points[1][1]
                maxdiff = points[1][0] - points[1][1]

                for i in range(2,len(points)):
                    sum = points[i][0] + points[i][1]
                    diff = points[i][0] - points[i][1]
                    if (sum < minsum):
                        minsum = sum
                    elif (sum > maxsum):
                        maxsum = sum
                    if (diff < mindiff):
                        mindiff = diff
                    elif (diff > maxdiff):
                        maxdiff = diff
            else:
                minsum = points[0][0] + points[0][1]
                maxsum = points[0][0] + points[0][1]
                mindiff = points[0][0] - points[0][1]
                maxdiff = points[0][0] - points[0][1]

                for i in range(1,len(points)):
                    if i== x :
                        continue
                    sum = points[i][0] + points[i][1]
                    diff = points[i][0] - points[i][1]
                    if (sum < minsum):
                        minsum = sum
                    elif (sum > maxsum):
                        maxsum = sum

                    if (diff < mindiff):
                        mindiff = diff

                    elif (diff > maxdiff):
                        maxdiff = diff


            mini = max(maxsum - minsum, maxdiff - mindiff)
            maxi = min(maxi, mini)



        return maxi
