class Solution {
    static class Point {
        int x, y, index;
        public Point(int x, int y, int index) {
            this.x = x;
            this.y = y;
            this.index = index;
        }
    }
    public int minimumDistance(int[][] pointsArray) {
        List<Point> points = new ArrayList<>();
        for (int i = 0; i < pointsArray.length; i++) {
            points.add(new Point(pointsArray[i][0], pointsArray[i][1], i));
        }

        List<Point> plus = new ArrayList<>();
        List<Point> minus = new ArrayList<>();
        for (Point p : points) {
            plus.add(new Point(p.x + p.y, 0, p.index));
            minus.add(new Point(p.x - p.y, 0, p.index));
        }

        plus.sort(Comparator.comparingInt(a -> a.x));
        minus.sort(Comparator.comparingInt(a -> a.x));

        Set<Integer> extremeIndices = new HashSet<>();
        extremeIndices.add(plus.get(0).index);
        extremeIndices.add(plus.get(plus.size() - 1).index);
        extremeIndices.add(minus.get(0).index);
        extremeIndices.add(minus.get(minus.size() - 1).index);

        int minDistance = Integer.MAX_VALUE;
        for (Integer skipIndex : extremeIndices) {
            int maxPlus = Integer.MIN_VALUE, minPlus = Integer.MAX_VALUE;
            int maxMinus = Integer.MIN_VALUE, minMinus = Integer.MAX_VALUE;
            for (Point p : points) {
                if (p.index == skipIndex) continue; // Skip the point
                int plusValue = p.x + p.y;
                int minusValue = p.x - p.y;
                maxPlus = Math.max(maxPlus, plusValue);
                minPlus = Math.min(minPlus, plusValue);
                maxMinus = Math.max(maxMinus, minusValue);
                minMinus = Math.min(minMinus, minusValue);
            }
            int currentMaxDistance = Math.max(maxPlus - minPlus, maxMinus - minMinus);
            minDistance = Math.min(minDistance, currentMaxDistance);
        }

        // Include the case where no points are removed
        int maxPlus = plus.get(plus.size() - 1).x - plus.get(0).x;
        int maxMinus = minus.get(minus.size() - 1).x - minus.get(0).x;
        minDistance = Math.min(minDistance, Math.max(maxPlus, maxMinus));

        return minDistance;
    }

}
