class Solution {
public:

    int calculate(vector<vector<int>>& points, int ignore){
        int minsum, maxsum, mindiff, maxdiff;
        if (ignore == 0){
            minsum = maxsum = points[1][0] + points[1][1];
            mindiff = maxdiff = points[1][0] - points[1][1];
        } else {
            minsum = maxsum = points[0][0] + points[0][1];
            mindiff = maxdiff = points[0][0] - points[0][1];
        }
        for (int i = 1; i < points.size(); i++) {
            if (i == ignore){
                continue;
            }
            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];
            if (sum < minsum){
                minsum = sum;
            }
            else if (sum > maxsum){
                maxsum = sum;
            }
            if (diff < mindiff){
                mindiff = diff;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
            }
        }
        return max(maxsum - minsum, maxdiff - mindiff);

    }

    int minimumDistance(vector<vector<int>>& points) {
        int minsum, maxsum, mindiff, maxdiff;
        int max_ind = 0, min_ind = 0;
        int max_ind1 = 0, min_ind1 = 0;
        minsum = maxsum = points[0][0] + points[0][1];
        mindiff = maxdiff = points[0][0] - points[0][1];
        for (int i = 1; i < points.size(); i++) {
            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];
            if (sum < minsum){
                minsum = sum;
                min_ind = i;
            }
            else if (sum > maxsum){
                maxsum = sum;
                max_ind = i;
            }
            if (diff < mindiff){
                mindiff = diff;
                min_ind1 = i;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
                max_ind1 = i;
            }
        }
        if(maxsum - minsum > maxdiff - mindiff){
            return min(calculate(points, max_ind), calculate(points, min_ind));
        }
        return min(calculate(points, max_ind1), calculate(points, min_ind1));
    }
};
