class Solution {
public:
	vector<vector<int>> points;
	vector<vector<pair<int,int>>>vis;
	int n;
	bool check(int val)
	{
		vector<pair<int, int>>ans;
		for (int i = 0; i < 4; ++i)
		{
			if (vis[i][n - 2].first - vis[i][1].first > val) return false;
			if (vis[i][n - 1].first - vis[i][0].first > val)
			{
				int xxx = vis[i][n - 1].second, yyy = vis[i][0].second;
				if (xxx > yyy) swap(xxx, yyy);
				ans.emplace_back(xxx, yyy);
			}
			if (vis[i][n - 1].first - vis[i][1].first > val)
			{
				int xxx = vis[i][n - 1].second, yyy = vis[i][1].second;
				if (xxx > yyy) swap(xxx, yyy);
				ans.emplace_back(xxx, yyy);
			}
			if (vis[i][n - 2].first - vis[i][0].first > val)
			{
				int xxx = vis[i][n - 2].second, yyy = vis[i][0].second;
				if (xxx > yyy) swap(xxx, yyy);
				ans.emplace_back(xxx, yyy);
			}

		}
		if (ans.size() == 0) return true;
		int res = ans[0].first;
		bool flag = true;
		for (int i = 1; i < ans.size(); ++i)
		{
			if (ans[i].first != res && ans[i].second != res)
			{
				flag = false;
			}
		}
		if (flag) return true;
		flag = true;
		res = ans[0].second;
		for (int i = 1; i < ans.size(); ++i)
		{
			if (ans[i].first != res && ans[i].second != res)
			{
				flag = false;
			}
		}
		return flag;
	}
	int minimumDistance(vector<vector<int>>& _points) {
		swap(points, _points);
		vis = vector<vector<pair<int,int>>>(4);
		auto cmp = [&](auto& a, auto& b) {return a[0] < b[0]; };
		sort(points.begin(), points.end(), cmp);
		for (int i=0;i<points.size() ;++i)
		{
			auto& point = points[i];
			vis[0].emplace_back(point[0] + point[1],i);
			vis[1].emplace_back(-point[0] + point[1],i);
			vis[2].emplace_back(point[0] - point[1],i);
			vis[3].emplace_back(-point[0] - point[1],i);
		}
		n = vis[0].size();
		for (int i = 0; i < 4; ++i)
		{
			sort(vis[i].begin(), vis[i].end());
		}

		int l = 0, r = 2 * 1e8;
		while (l < r)
		{
			int mid = l + r >> 1;
			if (check(mid)) r = mid;
			else l = mid + 1;

		}
		return l;
	}
};
