class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        //求出最大最小的x+y和x-y
        int n = points.size();
        int max_jia = -1e9, max_jia_idx, min_jia = 1e9, min_jia_idx, max_jian = -1e9, max_jian_idx, min_jian = 1e9, min_jian_idx;
        for (int i = 0; i < n; i++) {
            int x = points[i][0], y = points[i][1];
            if (max_jia < x + y) {
                max_jia = x + y;
                max_jia_idx = i;
            }
            if (min_jia > x + y) {
                min_jia = x + y;
                min_jia_idx = i;
            }
            if (max_jian < x - y) {
                max_jian = x - y;
                max_jian_idx = i;
            }
            if (min_jian > x - y) {
                min_jian = x - y;
                min_jian_idx = i;
            }
            // max_jia = max(max_jia, x + y);
            // min_jia = min(min_jia, x + y);
            // max_jian = max(max_jian, x - y);
            // min_jian = min(min_jian, x - y);
        }
        // cout << max(max_jia - min_jia, max_jian - min_jian) << endl;
        int ans = 1e9;
        vector<int> del({max_jia_idx,min_jia_idx,max_jian_idx,min_jian_idx});
        for (int k = 0; k < 4; k++) {
            int d = del[k];
            // cout << d << endl;
            max_jia = -1e9, max_jia_idx, min_jia = 1e9, min_jia_idx, max_jian = -1e9, max_jian_idx, min_jian = 1e9, min_jian_idx;
            for (int i = 0; i < n; i++) {
                if (i == d) continue;
                int x = points[i][0], y = points[i][1];
                if (max_jia < x + y) {
                    max_jia = x + y;
                    max_jia_idx = i;
                }
                if (min_jia > x + y) {
                    min_jia = x + y;
                    min_jia_idx = i;
                }
                if (max_jian < x - y) {
                    max_jian = x - y;
                    max_jian_idx = i;
                }
                if (min_jian > x - y) {
                    min_jian = x - y;
                    min_jian_idx = i;
                }
                // max_jia = max(max_jia, x + y);
                // min_jia = min(min_jia, x + y);
                // max_jian = max(max_jian, x - y);
                // min_jian = min(min_jian, x - y);
            }
            int res = max(max_jia - min_jia, max_jian - min_jian);
            // cout << res << endl;
            ans = min(ans, res);
        }
        return ans;

    }
};
