class Solution {
int MaxDist(vector<pair<int, int> >& A, int N)
{
	vector<pair<int,int>> V(N), V1(N);

	for (int i = 0; i < N; i++) {
		V[i] = {A[i].first + A[i].second,i};
		V1[i] = {A[i].first - A[i].second,i};
	}

	sort(V.begin(), V.end());
	sort(V1.begin(), V1.end());

	int mini = 1e9;

    for(int i = 0;i<N;i++){
        int vbck = V.back().first,vfrnt = V.front().first,v1bck = V1.back().first,v1frnt = V1.front().first;
        if(V.back().second == i){
           vbck = V[N-2].first;
        }
        if(V.front().second == i){
            vfrnt = V[1].first;
        }
        if(V1.back().second == i){
            v1bck = V1[N-2].first;
        }
        if(V1.front().second == i){
            v1frnt = V1[1].first;
        }
        mini = min (mini ,max(vbck - vfrnt, v1bck - v1frnt));
    }
	return mini;
}

public:
    int minimumDistance(vector<vector<int>>& points) {
        int N = points.size();
        vector<pair<int,int>> A;
        for(auto i : points){
            A.push_back({i[0],i[1]});
        }
        return MaxDist(A, N);
    }
};
