class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        set<pair<int, int>> a, b;
        int n = points.size();
        for (int i = 0; i < n; i++) {
            auto p = points[i];
            a.insert({p[0] + p[1], i});
            b.insert({p[0] - p[1], i});
        }
        int ans = 2e9;
        for (int i = 0; i < n; i++) {
            auto p = points[i];
            a.erase({p[0] + p[1], i});
            b.erase({p[0] - p[1], i});
            int res = max(a.rbegin()->first - a.begin()->first, b.rbegin()->first - b.begin()->first);
            ans = min(ans, res);
            a.insert({p[0] + p[1], i});
            b.insert({p[0] - p[1], i});
        }
        return ans;
    }
};
