class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
	long long a, b, c, d;
	long long ia = -1, ib = -1, ic = -1, id = -1;
	a = -1e9 + 7;
	c = -1e9 + 7;
	b = 1e9 + 7;
	d = 1e9 + 7;
	for (int i = 0;i<points.size();i++) {
		long long ta = points[i][0] + points[i][1];
		long long tb = points[i][0] - points[i][1];
		if (ta >= a) {
			ia = i;
			a = ta;
		}
		if (ta <= b) {
			ib = i;
			b = ta;
		}
		if (tb >= c) {
			ic = i;
			c = tb;
		}
		if (tb <= d) {
			id = i;
			d = tb;
		}
	}
	int c1;
	int c2;
	if (a - b >= c - d) {
		c1 = ia;
		c2 = ib;
	}
	else {
		c1 = ic;
		c2 = id;
	}

	long long ans = 1e9 + 7;
	a = -1e9 + 7;
	c = -1e9 + 7;
	b = 1e9 + 7;
	d = 1e9 + 7;
	for (int i = 0; i < points.size(); i++) {
		if (i == c1)continue;
		long long ta = points[i][0] + points[i][1];
		long long tb = points[i][0] - points[i][1];
		if (ta >= a) {
			ia = i;
			a = ta;
		}
		if (ta <= b) {
			ib = i;
			b = ta;
		}
		if (tb >= c) {
			ic = i;
			c = tb;
		}
		if (tb <= d) {
			id = i;
			d = tb;
		}
	}
	ans = min(max(a - b, c - d), ans);
	a = -1e9 + 7;
	c = -1e9 + 7;
	b = 1e9 + 7;
	d = 1e9 + 7;
	for (int i = 0; i < points.size(); i++) {
		if (i == c2)continue;
		long long ta = points[i][0] + points[i][1];
		long long tb = points[i][0] - points[i][1];
		if (ta >= a) {
			ia = i;
			a = ta;
		}
		if (ta <= b) {
			ib = i;
			b = ta;
		}
		if (tb >= c) {
			ic = i;
			c = tb;
		}
		if (tb <= d) {
			id = i;
			d = tb;
		}
	}
	ans = min(max(a - b, c - d), ans);
	return ans;
}
};
